import React, { Component } from 'react'

export default class GameBoard extends Component{
    renderBoard = () => {
        let layout = []
        for (let i = 0; i < this.props.boardState; i++){
            layout.push('X')
        }
        return layout
    }
    render(){
        return(
            <div>{this.renderBoard}</div>
        )
    }
}
