const boardReducer = (state = 0, action) => {
    switch (action.type){
        case 'CREATE_BOARD':
            let newData = Object.assign(state, {...action.payload})
            return {...state, ...newData}
        default:
            return state
    }
}

export default boardReducer

//payload will be determined by user input for board type
