import React, { Component } from 'react'
import { connect } from 'react-redux'

export default class GameBoard extends Component{
    renderBoard = () => {
        for(let i = 0; i < this.props.board; i++){
            return 'x'
        }
    }
    componentDidUpdate(){
        this.renderBoard()
    }
    render(){
        return(
            <div>{this.renderBoard()}</div>
        )
    }
}
